import{_ as a,c as l,o as i,ae as o}from"./chunks/framework.DI6M9D4h.js";const u=JSON.parse('{"title":"Beangle Commons Core 通用基础类4.1.0发布","description":"","frontmatter":{},"headers":[],"relativePath":"commons/release-4.1.0.md","filePath":"commons/release-4.1.0.md"}'),r={name:"commons/release-4.1.0.md"};function n(t,e,s,c,m,d){return i(),l("div",null,[...e[0]||(e[0]=[o('<h1 id="beangle-commons-core-通用基础类4-1-0发布" tabindex="-1">Beangle Commons Core 通用基础类4.1.0发布 <a class="header-anchor" href="#beangle-commons-core-通用基础类4-1-0发布" aria-label="Permalink to &quot;Beangle Commons Core 通用基础类4.1.0发布&quot;">​</a></h1><h2 id="_1-增强" tabindex="-1">1.增强 <a class="header-anchor" href="#_1-增强" aria-label="Permalink to &quot;1.增强&quot;">​</a></h2><ul><li>增加了spi注解</li><li>增加了description注解，以及绑定支持</li><li>支持PlaceHolder及其默认值</li><li>Arrays中增加了isBlank，concat 方法</li><li>增加了ResourceResolver，支持按照模式查找资源</li><li>增加了Locales解析类</li><li>增加了BeanManifest专门用于采集属性</li><li>支持Injection</li><li>增加了更多的mimetype</li><li>Strings增加了stripEnd方法，用于删除尾部的字符串</li><li>增加了Des算法支持</li></ul><h2 id="_2-重构和向后不兼容改变" tabindex="-1">2.重构和向后不兼容改变 <a class="header-anchor" href="#_2-重构和向后不兼容改变" aria-label="Permalink to &quot;2.重构和向后不兼容改变&quot;">​</a></h2><ul><li>删除了PropertyTransformer，UnaryFunction，BinaryFunction用scala原生的接口取代</li><li>重构了Text Bundle的目录结构，删除了spi包，采用spi注解的方式</li><li>重构了静态资源过滤器，过滤器接口去除了预先对出的字节数组</li><li>绑定bean时默认的LazyInit=true改为了false，有助于发现一些循环依赖错误。</li><li>重命名ContainerHook为ContainerRefreshedHook</li><li>删除了ClassInfo中关于属性的支持，这部分移动到BeanManifest</li><li>将xml形式的模块定义，转换成properties</li><li>AbstractBindModule的方法由doBinding改为binding</li><li>基于scala 2.11</li></ul><h2 id="_3-修复错误" tabindex="-1">3.修复错误 <a class="header-anchor" href="#_3-修复错误" aria-label="Permalink to &quot;3.修复错误&quot;">​</a></h2><ul><li>修复了TextFormat中的错误</li><li>修复数字转换错误(convertor error)</li><li>修复 Objects array equals error</li></ul><h1 id="beangle-commons-web" tabindex="-1">Beangle Commons Web <a class="header-anchor" href="#beangle-commons-web" aria-label="Permalink to &quot;Beangle Commons Web&quot;">​</a></h1><h2 id="_1-增强-1" tabindex="-1">1.增强 <a class="header-anchor" href="#_1-增强-1" aria-label="Permalink to &quot;1.增强&quot;">​</a></h2><ul><li>新的web模块化支持(Initializer,web-module.properties)</li><li>增加了ServletContextHolder</li></ul><h2 id="_2-重构和向后不兼容改变-1" tabindex="-1">2.重构和向后不兼容改变 <a class="header-anchor" href="#_2-重构和向后不兼容改变-1" aria-label="Permalink to &quot;2.重构和向后不兼容改变&quot;">​</a></h2><ul><li>UrlRender的渲染方法中增加了context参数</li></ul>',12)])])}const _=a(r,[["render",n]]);export{u as __pageData,_ as default};
